
public class tipoVariableYcast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			
		double variable1=2430.89;
		int variable1Entero=(int) variable1; //cast
		//hace cast para que cambie el double de la variable por el int
		//y pueda entrar en el resultado
		
		System.out.println(variable1Entero);
		
		
		long prueba=12222222222222222L;
		short numeroPequeno=13555;
		byte numeroAunMasPequeno=12;
		float numeroDecimalpqueno=2.5F;
		
		
		
		int resultado=(int)variable1+variable1Entero;
		System.out.println(resultado);

		
	}

}
